import "./App.css";
import { Routes, Route, BrowserRouter as Router } from "react-router-dom";
import Dashboard from "./Dashboard";
import {
  Card,
  CardContent,
  Typography,
  Button,
  CardMedia,
} from "@mui/material";
import { useTheme } from "@mui/material/styles";
import MobileStepper from "@mui/material/MobileStepper";
import KeyboardArrowLeft from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRight from "@mui/icons-material/KeyboardArrowRight";
import React, { useEffect, useState } from "react";

const projects = [
  {
    title: "Project 1",
    description: "This is the description for project 1.",
    imageUrl:
      "https://images.pexels.com/photos/416405/pexels-photo-416405.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2s",
  },
  {
    title: "Project 2",
    description: "This is the description for project 2.",
    imageUrl:
      "https://images.pexels.com/photos/3183150/pexels-photo-3183150.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
  },
  {
    title: "Project 3",
    description: "This is the description for project 3.",
    imageUrl:
      "https://images.pexels.com/photos/941555/pexels-photo-941555.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2",
  },
  // Add more projects as needed
];

const Carousel = () => {
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const maxSteps = projects.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  return (
    <div>
      <Card  style={{ 
    backgroundColor: projects[activeStep].color, 
    width: '250px', 
    height: '140px', 
    overflow: 'hidden' 
}}>
        <CardMedia
          component="img"
          image={projects[activeStep].imageUrl}
          alt={projects[activeStep].title}
        />
        <CardContent>
          <Typography variant="h5" component="div">
            {projects[activeStep].title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {projects[activeStep].description}
          </Typography>
        </CardContent>
      </Card>
      <MobileStepper
        steps={maxSteps}
        position="static"
        variant="dots"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
          >
            Next
            {theme.direction === "rtl" ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === "rtl" ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <h1>My Projects</h1>
      <Carousel />
      {/* Other components or content can be added here */}
    </div>
  );
};
export default App;
